INSERT INTO `article` (`id`, `abstract`, `author`, `background_url`, `comment_count`, `comment_status`, `create_time`, `delete_time`, `is_original`, `is_pinned`, `link_name`, `name`, `ping_status`, `pinged`, `share_count`, `status`, `target`, `thumb_count`, `to_ping`, `update_time`, `view_count`, `is_recommended`) VALUES (8, '<p>众所周知，coursera上有很多很好但很贵的课，比如Andrew的机器学习，IBM的数据可视化，斯坦福的算法专题（强推）。不过好在coursera有个助学金系统，写好申请原因基本就能白嫖成功。所以下面是是一份Coursera助学金的申请模板', '头秃的whalien', 'https://images.unsplash.com/photo-1593642632559-0c6d3fc62b89?ixid=MnwxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1469&q=80', 332, 1, '2021-08-08 03:23:12', NULL, 1, 1, '/articles/coursera-financial-aid-application-template', 'Coursera助学金申请模板', NULL, NULL, 2131, 1, '_self', 12334, NULL, '2021-10-25 22:33:53', 23232, 1);
INSERT INTO `article` (`id`, `abstract`, `author`, `background_url`, `comment_count`, `comment_status`, `create_time`, `delete_time`, `is_original`, `is_pinned`, `link_name`, `name`, `ping_status`, `pinged`, `share_count`, `status`, `target`, `thumb_count`, `to_ping`, `update_time`, `view_count`, `is_recommended`) VALUES (9, '当你老了歌词,《当你老了》是一首由赵照对爱尔兰诗人威廉·勃特勒·叶芝的同名诗作改编而成的歌曲。2015年2月18日，莫文蔚阔别11年再登春晚，献唱《当你老了》，也是她2015年发行的EP单曲 。这首歌曲的创作是源于赵照对母亲的感情，之前一直“北漂”做音乐，赵照很少给母亲唱过歌。在偶然读到爱尔兰诗人叶芝的诗歌《when you are old》时，他忽然想到了自己的妈妈在窗户下灯光昏黄的模样，有感而发写成了这首歌曲。歌词出自于爱尔兰诗人叶芝的诗作。', '既要写前端也要写后端真的哭了的whalien', 'https://images.unsplash.com/photo-1522075782449-e45a34f1ddfb?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2070&q=80', 2323, 1, '2021-08-01 22:55:56', NULL, 1, 1, '/articles/when-you-are-old', '당신이 늙었을 때', NULL, NULL, 1234324, 1, '_self', 1342, NULL, '2021-10-25 23:00:03', 211, 1);
INSERT INTO `article` (`id`, `abstract`, `author`, `background_url`, `comment_count`, `comment_status`, `create_time`, `delete_time`, `is_original`, `is_pinned`, `link_name`, `name`, `ping_status`, `pinged`, `share_count`, `status`, `target`, `thumb_count`, `to_ping`, `update_time`, `view_count`, `is_recommended`) VALUES (10, '《致橡树》是“朦胧派五将”之一舒婷的作品，创作于1977年3月，是文革过后最早发表的爱情诗。在此之前，旧式诗文中常常用青藤缠树、花叶依风等纤柔、妩媚的形象来描绘情爱关系中的女性；而诗人别具一格地选择了“木棉”与“橡树”两个中心意象，向世人展示了新一代女性心目中“伟大的爱情”——扎根于同一片土地之上，同甘共苦、冷暖相依。', '前端、后端、Devop都在做的我真哭了', 'http://images.china.cn/attachement/jpg/site1007/20110221/00114320def10ecc6f5511.jpg', 12, 1, '2021-07-01 23:04:32', NULL, 1, 0, '/articles/to-the-oak-tree', '如果我爱你', NULL, NULL, 1323, 1, '_self', 1242, NULL, '2021-10-25 23:05:30', 2323, 1);
